/**
 * ServiceImpl object for domain model class AuctionStat.
 *
 * @see AuctionStat
 */
@Service("platform.AuctionStatService")
public class AuctionStatServiceImpl implements AuctionStatService {

    private static final Logger LOGGER = LoggerFactory.getLogger(AuctionStatServiceImpl.class);

    @Autowired
    @Qualifier("platform.AuctionStatDao")
    private WMGenericDao<AuctionStat, AuctionStatId> wmGenericDao;

    public void setWMGenericDao(WMGenericDao<AuctionStat, AuctionStatId> wmGenericDao) {
        this.wmGenericDao = wmGenericDao;
    }

	@Transactional(readOnly = true, value = "platformTransactionManager")
	@Override
	public AuctionStat getById(AuctionStatId auctionstatId) throws EntityNotFoundException {
        LOGGER.debug("Finding AuctionStat by id: {}", auctionstatId);
        AuctionStat auctionstat = this.wmGenericDao.findById(auctionstatId);
        if (auctionstat == null){
            LOGGER.debug("No AuctionStat found with id: {}", auctionstatId);
            throw new EntityNotFoundException(String.valueOf(auctionstatId));
        }
        return auctionstat;
    }

	@Transactional(readOnly = true, value = "platformTransactionManager")
	@Override
	public Page<AuctionStat> findAll(QueryFilter[] queryFilters, Pageable pageable) {
        LOGGER.debug("Finding all AuctionStats");
        return this.wmGenericDao.search(queryFilters, pageable);
    }

    @Transactional(readOnly = true, value = "platformTransactionManager")
    @Override
    public Page<AuctionStat> findAll(String query, Pageable pageable) {
        LOGGER.debug("Finding all AuctionStats");
        return this.wmGenericDao.searchByQuery(query, pageable);
    }

    @Transactional(readOnly = true, value = "platformTransactionManager")
    @Override
    public Downloadable export(ExportType exportType, String query, Pageable pageable) {
        LOGGER.debug("exporting data in the service platform for table AuctionStat to {} format", exportType);
        return this.wmGenericDao.export(exportType, query, pageable);
    }

	@Transactional(readOnly = true, value = "platformTransactionManager")
	@Override
	public long count(String query) {
        return this.wmGenericDao.count(query);
    }

    @Transactional(readOnly = true, value = "platformTransactionManager")
    @SuppressWarnings("unchecked")
	@Override
    public Page<AuctionStat> findAssociatedValues(Object value, String entityName, String key,  Pageable pageable) {
        LOGGER.debug("Fetching all associated");
        return this.wmGenericDao.getAssociatedObjects(value, entityName, key, pageable);
    }
}

